// returns a string like http://host or https://host:4711 without trailing slash

// or if all fails an empty string

function getURIPrefix()

{

  // elegant

  if (isset($_SERVER['SCRIPT_URI']))

  {

    $su=$_SERVER['SCRIPT_URI'];

    $arr=parse_url($su);

    $pre=$arr['scheme'].'://'.$arr['host'];

    if (isset($arr['port']))

      $pre.=':'.$arr['port'];

    return $pre;

  }



  // long winded

  if ((isset($_SERVER['HTTPS']) && $_SERVER['HTTPS']=='on') // (isset($_SERVER['SERVER_PORT']) && $_SERVER['SERVER_PORT']==443))

  {

    $pre='https://';

    $stdport=443;

  }

  else

  {

    $pre='http://';

    $stdport=80;

  }



  if (isset($_SERVER['HTTP_HOST']))

  {

    $pre.=$_SERVER['HTTP_HOST'];

  }

  else

  {

    if (isset($_SERVER['SERVER_NAME']))

      $pre.=$_SERVER['SERVER_NAME'];

    else

      return '';

   }



  if (isset($_SERVER['SERVER_PORT']) && $_SERVER['SERVER_PORT']!=$stdport)

    $pre.=':'.$_SERVER['SERVER_PORT'];



  return $pre;    

}



