$eventcopy->timestart = ($eventcopy->timestart+DAYSECS*$this->properties->repeatdays) + dst_offset_on($eventcopy->timestart) - dst_offset_on($eventcopy->timestart+DAYSECS*$this->properties->repeatdays);


